import React, { useState } from 'react';
import { useAuth } from '../AuthContext';
import Header from '../components/Header';

const LoginPage = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const { login } = useAuth();

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');

    // Basic validation
    if (!email || !password) {
      setError('Please fill all fields');
      return;
    }

    // Email validation
    if (!email.includes('@')) {
      setError('Please enter a valid email');
      return;
    }

    // Password validation (minimum 6 characters)
    if (password.length < 6) {
      setError('Password must be at least 6 characters');
      return;
    }

    // Simple hardcoded check (aap baad mein change kar sakte hain)
    if (email === 'user@example.com' && password === 'password') {
      login();
      alert('Login Successful!');
    } else {
      setError('Invalid email or password');
    }
  };

  return (
    <div>
      <Header />
      <div style={{ 
        padding: '4rem 2rem', 
        minHeight: '60vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center'
      }}>
        <div style={{
          background: 'white',
          padding: '2rem',
          borderRadius: '10px',
          boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
          width: '100%',
          maxWidth: '400px'
        }}>
          <h2 style={{ textAlign: 'center', marginBottom: '2rem' }}>
            {isLogin ? 'Login' : 'Register'}
          </h2>
          
          {error && (
            <div style={{
              color: 'red',
              textAlign: 'center',
              marginBottom: '1rem',
              padding: '0.5rem',
              background: '#ffe6e6',
              borderRadius: '5px'
            }}>
              {error}
            </div>
          )}
          
          <form onSubmit={handleSubmit}>
            <input 
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              style={{
                width: '100%',
                padding: '0.8rem',
                marginBottom: '1rem',
                border: '1px solid #ddd',
                borderRadius: '5px'
              }}
            />
            
            <input 
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={{
                width: '100%',
                padding: '0.8rem',
                marginBottom: '1.5rem',
                border: '1px solid #ddd',
                borderRadius: '5px'
              }}
            />
            
            <button 
              type="submit"
              style={{
                width: '100%',
                padding: '0.8rem',
                background: '#007bff',
                color: 'white',
                border: 'none',
                borderRadius: '5px',
                fontSize: '1rem'
              }}
            >
              {isLogin ? 'Login' : 'Register'}
            </button>
          </form>
          
          <p style={{ textAlign: 'center', marginTop: '1rem' }}>
            {isLogin ? "Don't have an account? " : "Already have an account? "}
            <button 
              onClick={() => {
                setIsLogin(!isLogin);
                setError('');
                setEmail('');
                setPassword('');
              }}
              style={{
                background: 'none',
                border: 'none',
                color: '#007bff',
                cursor: 'pointer'
              }}
            >
              {isLogin ? 'Register' : 'Login'}
            </button>
          </p>

          {/* Demo credentials */}
          <div style={{
            marginTop: '2rem',
            padding: '1rem',
            background: '#f8f9fa',
            borderRadius: '5px',
            fontSize: '0.8rem',
            textAlign: 'center'
          }}>
            <strong>Demo Credentials:</strong><br/>
            Email: user@example.com<br/>
            Password: password
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
