import React, { useState } from 'react';
import { useAuth } from '../AuthContext';
import { useNavigate } from 'react-router-dom';
import './DashboardLayout.css';

const DashboardLayout = ({ children }) => {
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [activeTab, setActiveTab] = useState('home');
  const { currentUser, registeredUsers, logout } = useAuth();
  const navigate = useNavigate();

  // Online logos - aap koi bhi use kar sakte hain
  const onlineLogos = [
    "https://cdn-icons-png.flaticon.com/512/2170/2170153.png", // Simple logo 1
    "https://cdn-icons-png.flaticon.com/512/732/732212.png",   // Simple logo 2  
    "https://cdn-icons-png.flaticon.com/512/5968/5968292.png", // Company logo
    "https://cdn-icons-png.flaticon.com/512/1006/1006771.png", // Shield logo
    "https://cdn-icons-png.flaticon.com/512/3573/3573756.png"  // Modern logo
  ];

  const [selectedLogo] = useState(onlineLogos[0]); // Pehla logo use karenge

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <div className="dashboard-layout">
      {/* Top Navbar */}
      <nav className="dashboard-navbar">
        <div className="nav-left">
          <img 
            src={selectedLogo} 
            alt="Logo" 
            className="nav-logo"
            onError={(e) => {
              // Agar logo load nahi hota toh fallback
              e.target.src = "https://cdn-icons-png.flaticon.com/512/2170/2170153.png";
            }}
          />
          <span className="nav-brand"></span>
        </div>
        <div className="nav-right">
          <span className="welcome-text">
            Welcome, {currentUser?.firstName}!
          </span>
          <button className="logout-btn" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </nav>

      <div className="dashboard-container">
        {/* Sidebar */}
        <aside className={`sidebar ${sidebarOpen ? 'open' : 'closed'}`}>
          <div className="sidebar-content">
            <h3 className="sidebar-title">Menu</h3>
            
            <div className="sidebar-menu">
              <button 
                className={`menu-item ${activeTab === 'home' ? 'active' : ''}`}
                onClick={() => setActiveTab('home')}
              >
                üè† <span>Home</span>
              </button>
              
              <button 
                className={`menu-item ${activeTab === 'users' ? 'active' : ''}`}
                onClick={() => setActiveTab('users')}
              >
                üë• <span>Users</span>
              </button>

              <button 
                className={`menu-item ${activeTab === 'settings' ? 'active' : ''}`}
                onClick={() => setActiveTab('settings')}
              >
                ‚öôÔ∏è <span>Settings</span>
              </button>
            </div>

            <div className="sidebar-toggle" onClick={() => setSidebarOpen(!sidebarOpen)}>
              {sidebarOpen ? '‚óÄ' : '‚ñ∂'}
            </div>
          </div>
        </aside>

        {/* Main Content */}
        <main className="dashboard-main">
          {activeTab === 'home' && (
            <div className="tab-content">
              <h2>Welcome, {currentUser?.firstName}!</h2>
              <div className="user-card">
                <h3>Your Profile</h3>
                <div className="profile-info">
                  <p><strong>Name:</strong> {currentUser?.firstName} {currentUser?.lastName}</p>
                  <p><strong>Email:</strong> {currentUser?.email}</p>
                  <p><strong>Mobile:</strong> {currentUser?.mobileNumber}</p>
                  <p><strong>Login Time:</strong> {currentUser?.loginTime}</p>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'users' && (
            <div className="tab-content">
              <div className="tab-header">
                <h2>Registered Users</h2>
                <span className="users-count">{registeredUsers.length} users</span>
              </div>
              
              {registeredUsers.length === 0 ? (
                <div className="no-users">
                  <p>No users registered yet.</p>
                </div>
              ) : (
                <div className="users-grid">
                  {registeredUsers.map(user => (
                    <div key={user.id} className="user-card">
                      <div className="user-avatar">
                        {user.firstName?.charAt(0)}{user.lastName?.charAt(0)}
                      </div>
                      <h3>{user.firstName} {user.lastName}</h3>
                      <p><strong>Email:</strong> {user.email}</p>
                      <p><strong>Mobile:</strong> {user.mobileNumber}</p>
                      <p><strong>Registered:</strong> {user.loginTime}</p>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}

          {activeTab === 'settings' && (
            <div className="tab-content">
              <h2>Settings</h2>
              <div className="settings-card">
                <h3>Account Settings</h3>
                <p>Manage your account preferences and settings here.</p>
                <div className="setting-option">
                  <label>Notifications</label>
                  <input type="checkbox" defaultChecked />
                </div>
                <div className="setting-option">
                  <label>Theme</label>
                  <select>
                    <option>Light</option>
                    <option>Dark</option>
                  </select>
                </div>
              </div>
            </div>
          )}
        </main>
      </div>
    </div>
  );
};

export default DashboardLayout;
