import React, { useState, useEffect } from 'react';
import { Home, Users, Settings, LogOut, Menu, X, DollarSign } from 'lucide-react';

// --- MOCK DEPENDENCIES ---
// Since we cannot use react-router-dom or a real AuthContext in a single file,
// we create mock versions to make the component runnable.

const mockCurrentUser = {
  uid: 'user-123',
  email: 'mock.user@example.com',
  name: 'Ajeet Kumar',
};

// Mock Auth Hook
const useAuth = () => ({
  currentUser: mockCurrentUser,
  logout: () => console.log("Mock Logout called"),
});

// Mock Navigation Hook
const useNavigate = () => (path) => console.log(`Navigating to: ${path}`);

// --- COMPONENTS ---

// 1. Logo SVG Placeholder
const DashboardLogo = () => (
  <div className="flex items-center space-x-2">
    <DollarSign className="w-8 h-8 text-indigo-500" />
    <span className="text-xl font-bold text-gray-800 tracking-wider">
      FinTrack Pro
    </span>
  </div>
);

// 2. Sidebar Component
const Sidebar = ({ isOpen, setActiveTab, activeTab, closeSidebar }) => {
  const navItems = [
    { name: 'Home', icon: Home, key: 'home' },
    { name: 'Reports', icon: Users, key: 'reports' },
    { name: 'Budget', icon: DollarSign, key: 'budget' },
    { name: 'Settings', icon: Settings, key: 'settings' },
  ];

  const baseClasses = "flex items-center p-3 rounded-xl transition-all duration-200 cursor-pointer text-gray-700 hover:bg-indigo-100 hover:text-indigo-600";
  const activeClasses = "bg-indigo-600 text-white shadow-lg hover:bg-indigo-700 hover:text-white";

  return (
    <div
      className={`fixed top-0 left-0 h-full bg-white shadow-xl p-4 transform ${
        isOpen ? 'translate-x-0' : '-translate-x-full'
      } lg:relative lg:translate-x-0 transition-transform duration-300 z-40 w-64 flex flex-col`}
    >
      <div className="flex justify-between items-center mb-8 lg:hidden">
        <DashboardLogo />
        <button onClick={closeSidebar} className="p-2 rounded-full hover:bg-gray-100">
          <X className="w-6 h-6 text-gray-500" />
        </button>
      </div>

      <nav className="space-y-2 flex-grow">
        {navItems.map(item => (
          <div
            key={item.key}
            className={`${baseClasses} ${activeTab === item.key ? activeClasses : ''}`}
            onClick={() => {
              setActiveTab(item.key);
              closeSidebar(); // Close on mobile navigation
            }}
          >
            <item.icon className="w-5 h-5 mr-3" />
            <span className="font-medium">{item.name}</span>
          </div>
        ))}
      </nav>
    </div>
  );
};

// 3. Dashboard Content Component (Mock Children)
const DashboardContent = ({ activeTab }) => {
  const contentMap = {
    home: { title: 'Dashboard Overview', message: 'Welcome back! Your finances are looking great this month.' },
    reports: { title: 'Financial Reports', message: 'Detailed analysis of Q3 performance.' },
    budget: { title: 'Monthly Budget', message: 'Review and adjust your current spending limits.' },
    settings: { title: 'User Settings', message: 'Manage your profile and preferences.' },
  };

  const currentContent = contentMap[activeTab] || contentMap.home;

  return (
    <main className="flex-1 p-6 sm:p-10 bg-gray-50 min-h-screen">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-4xl font-extrabold text-gray-800 mb-4">{currentContent.title}</h1>
        <p className="text-gray-600 mb-8">{currentContent.message}</p>
        <div className="bg-white p-6 rounded-2xl shadow-xl border border-gray-200">
          <h2 className="text-2xl font-semibold text-indigo-600 mb-4">Content Area for "{activeTab}"</h2>
          <p className="text-gray-700">This section would contain the specific UI for the selected tab. For example, charts, forms, or tables.</p>
          <div className="mt-6 h-48 bg-gray-100 rounded-lg flex items-center justify-center text-gray-400">
            {activeTab.toUpperCase()} Data Visualization Placeholder
          </div>
        </div>
      </div>
    </main>
  );
};

// 4. Main Layout Component (DashboardLayout equivalent logic)
const App = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [activeTab, setActiveTab] = useState('home');
  const { currentUser, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const toggleSidebar = () => setSidebarOpen(prev => !prev);
  const closeSidebar = () => setSidebarOpen(false);

  // Close sidebar on large screens when resized from small screen
  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth >= 1024) { // Tailwind's 'lg' breakpoint
        setSidebarOpen(true);
      } else {
        setSidebarOpen(false);
      }
    };

    handleResize(); // Initial check
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <div className="flex bg-gray-50">
      {/* Sidebar */}
      <div className="hidden lg:block">
         {/* Sidebar is fixed on desktop, so its width controls the main content's position */}
         <Sidebar
            isOpen={true} // Always open on desktop
            setActiveTab={setActiveTab}
            activeTab={activeTab}
            closeSidebar={() => {}} // No-op on desktop
          />
      </div>

      {/* Mobile Sidebar Overlay */}
      <div
        className={`fixed inset-0 bg-black bg-opacity-50 z-30 transition-opacity duration-300 ${
          sidebarOpen ? 'opacity-100' : 'opacity-0 pointer-events-none'
        } lg:hidden`}
        onClick={closeSidebar}
      ></div>

      {/* Main Content Area */}
      <div className="flex flex-col flex-1 w-full lg:ml-64"> {/* lg:ml-64 matches sidebar width on desktop */}
        
        {/* Top Navbar */}
        <header className="sticky top-0 z-20 bg-white shadow-md p-4 flex justify-between items-center h-16">
          
          {/* LEFT SIDE: Logo & Menu Toggle (Problem Solved Here) */}
          <div className="flex items-center space-x-4">
            <button
              onClick={toggleSidebar}
              className="p-2 rounded-full hover:bg-gray-100 lg:hidden" // Only visible on mobile
            >
              <Menu className="w-6 h-6 text-gray-600" />
            </button>
            <DashboardLogo />
          </div>

          {/* RIGHT SIDE: User Info & Logout */}
          <div className="flex items-center space-x-4">
            <span className="text-sm font-medium text-gray-500 hidden sm:block">
              Hello, {currentUser.name.split(' ')[0]}
            </span>
            <button
              className="bg-red-500 hover:bg-red-600 text-white font-semibold py-2 px-4 rounded-xl transition duration-150 shadow-md flex items-center text-sm"
              onClick={handleLogout}
            >
              <LogOut className="w-4 h-4 mr-2" />
              Logout
            </button>
          </div>
        </header>

        {/* Content */}
        <DashboardContent activeTab={activeTab} />
      </div>

      {/* Re-render the mobile sidebar on top of everything for the fixed/overlay effect */}
      <Sidebar
        isOpen={sidebarOpen}
        setActiveTab={setActiveTab}
        activeTab={activeTab}
        closeSidebar={closeSidebar}
      />
    </div>
  );
};

export default App;
