import React, { useState } from 'react';
import { useAuth } from '../AuthContext';
import Header from '../components/Header';

const LoginPage = () => {
  const [isLogin, setIsLogin] = useState(true);
  const { login } = useAuth(); // ✅ AuthContext se login function import karo

  const handleSubmit = (e) => {
    e.preventDefault();
    login(); // ✅ Ye function call karo
    alert('Login Successful!');
  };

  return (
    <div>
      <Header />
      <div style={{ 
        padding: '4rem 2rem', 
        minHeight: '60vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center'
      }}>
        <div style={{
          background: 'white',
          padding: '2rem',
          borderRadius: '10px',
          boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
          width: '100%',
          maxWidth: '400px'
        }}>
          <h2 style={{ textAlign: 'center', marginBottom: '2rem' }}>
            {isLogin ? 'Login' : 'Register'}
          </h2>
          
          <form onSubmit={handleSubmit}> {/* ✅ onSubmit add karo */}
            {!isLogin && (
              <input 
                type="text"
                placeholder="Full Name"
                style={{
                  width: '100%',
                  padding: '0.8rem',
                  marginBottom: '1rem',
                  border: '1px solid #ddd',
                  borderRadius: '5px'
                }}
              />
            )}
            
            <input 
              type="email"
              placeholder="Email"
              style={{
                width: '100%',
                padding: '0.8rem',
                marginBottom: '1rem',
                border: '1px solid #ddd',
                borderRadius: '5px'
              }}
            />
            
            <input 
              type="password"
              placeholder="Password"
              style={{
                width: '100%',
                padding: '0.8rem',
                marginBottom: '1.5rem',
                border: '1px solid #ddd',
                borderRadius: '5px'
              }}
            />
            
            <button 
              type="submit" // ✅ type="submit" rahega
              style={{
                width: '100%',
                padding: '0.8rem',
                background: '#007bff',
                color: 'white',
                border: 'none',
                borderRadius: '5px',
                fontSize: '1rem'
              }}
            >
              {isLogin ? 'Login' : 'Register'}
            </button>
          </form>
          
          <p style={{ textAlign: 'center', marginTop: '1rem' }}>
            {isLogin ? "Don't have an account? " : "Already have an account? "}
            <button 
              onClick={() => setIsLogin(!isLogin)}
              style={{
                background: 'none',
                border: 'none',
                color: '#007bff',
                cursor: 'pointer'
              }}
            >
              {isLogin ? 'Register' : 'Login'}
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
